import React, { Component } from "react";
import { Input, FormBtn } from "../components/Form";
import Container from "../components/Container/Index";
import './pages.css';

class Login extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
           
    };
        

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleChange(event) {
        this.setState({
            name: event.target.name,
            
        });
    };

    handleSubmit(event){
        event.preventDefault();
        alert(this.state.name);
        
    };
  

    render() {

        return (
            <Container>
                <form id="loginForm" onSubmit ={this.handleSubmit}>
                    <h4>User Name</h4>
                    <Input type ='text' name ={this.state.name} onChange={this.handleChange}></Input>
                    <h4>Password</h4>
                    <Input type ='text' password ={this.state.password} onChange ={this.handleChange}></Input>
                    <FormBtn>Submit</FormBtn>
                </form>
                <div id="register">Don't have an account? <a id="account" href="/newUser">Click Here</a> to create one.</div>              
            </Container>   
            
        );
    }

}

export default Login;
